import React from "react"
import Die from "./Die"
import { nanoid } from "nanoid"

export default function App() {
  const [dice, setDice] = React.useState(allNewDie)
  function generateDieInstance(){
    return {
      value: Math.ceil(Math.random()*6),
      isHeld: false,
      id: nanoid() 
    }
  }
  function allNewDie(){
    const newDice = []
    for (let i = 0 ; i<10 ; i++) {
      newDice.push(generateDieInstance())
    }
    return newDice
  }

  function holdDie(id) {
    //find this id's obj in the dice arrOfObj & flip isHeld
    setDice(prevDice => prevDice.map(dieObj => {
      return dieObj.id===id ? 
        {
          ...dieObj ,
          isHeld: !dieObj.isHeld
        } :
        dieObj
      }
    ))
  }
  const dieElements = dice.map(dieObj => (
    <Die 
      key={dieObj.id} 
      value={dieObj.value} 
      isHeld={dieObj.isHeld}
      holdDie={() => holdDie(dieObj.id)}
    />
  ))
  function rollDie() {
    setDice(prevDie => prevDie.map(prevDieObj => {
      return prevDieObj.isHeld ? 
        prevDieObj : 
        generateDieInstance()
    }))
  }

  return (
    <main>
      <h1 className="title">Tenzies</h1>
            <p className="instructions">
              Roll until all dice are the same. Click each die to freeze it at its current value between rolls.
            </p>
      <div className="dice-container">
        {dieElements}
      </div>
      <button
        className="roll-dice"
        onClick={rollDie}
      >
        Roll Dice
      </button>
    </main>
  )
}
