import React from "react"
import Die from "./Die"
import { nanoid } from "nanoid"

export default function App() {
  const [dice, setDice] = React.useState(generateNewDice)
  function generateNewDice(){
    const newDice = []
    for (let i = 0 ; i<10 ; i++) {
      newDice.push({
        value: Math.ceil(Math.random()*6),
        isHeld: true,
        id: nanoid() 
      })
    }
    return newDice
  }

  function holdDie(id) {
    //find this id's obj in the dice arrOfObj & flip isHeld

    setDice
  }
  const dieElements = dice.map(dieObj => (
    <Die 
      key={dieObj.id} 
      value={dieObj.value} 
      isHeld={dieObj.isHeld}
      holdDie={() => holdDie(dieObj.id)}
    />
  ))
  function handleClick() {
    setDice(prevState => generateNewDice())
  }

  return (
    <main>
      <div className="dice-container">
        {dieElements}
      </div>
      <button
        className="roll-dice"
        onClick={handleClick}
      >
        Roll Dice
      </button>
    </main>
  )
}
