import React from "react"
import Die from "./Die"
import { nanoid } from "nanoid"

export default function App() {
  const [dice, setDice] = React.useState(generateNewDice)

  function generateNewDice(){
    const newDice = []
    for (let i = 0 ; i<10 ; i++) {
      newDice.push({
        value: Math.ceil(Math.random()*6),
        isHeld: false
      })
    }
    return newDice
  }

  const dieElements = dice.map(dieObj=> <Die value={dieObj.value}/>)

  function handleClick() {
    setDice(prevState => generateNewDice())
  }

  return (
    <main>
      <div className="dice-container">
        {dieElements}
      </div>
      <button
        className="roll-dice"
        onClick={handleClick}
      >
        Roll Dice
      </button>
    </main>
  )
}
