import React from "react"

export default function Die(props) {
    const styles = {backgroundColor : props.isHeld ? "#59E391" : "none"}
    return (
        <div 
            className="die-face" 
            style={styles} 
            onClick={props.func(props.key)}
        >
            <h2 className="die-num">{props.value}</h2>
        </div>
    )
}
